import React from 'react';
import ReactPlayer from 'react-player/lazy';

const Card = (props) => (
  <div className="body"
  >
    {props.sample.cover && (
      <img className='img'
        src={props.sample.cover}
        alt=""
      />
    )}
    {props.sample.webPreviewUrl && (
      <ReactPlayer className
        width="100%"
        height="100%"
        url={props.sample.webPreviewUrl}
        controls
      />
    )}
    <div className="ml-2 mt-1">
      <div className="mb-2">
        {props.sample.name}
      </div>
      {props.sample.link && (
        <a
          className="text-dark-500 hover:text-blue-600"
          href={props.sample.link}
          target="_blank"
          rel="noopener noreferrer"
        >
          Show the resource
        </a>
      )}

    </div>
  </div>
);

export default Card;
